package com.java.BasicsProgram;

public class LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean a=true;
		boolean b=false;
		
		boolean c= a&&b; //Here double (&&)And operator represents logical operator.
		
		boolean d= a||b; //Here double (||)or operator represents logical operator.
		
		boolean e= !b;  //Here (!)not represnts logical operator.
		
		System.out.println(c);
		System.out.println(d);
		System.out.println(e); 

	}

}
